import React, { useState, useEffect } from 'react';
import Input from '../../util/FormElements/Input';
import "./Styles.css";

// Expresión regular para validar el formato de correo electrónico
const emailRegex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;

export default function FirstStep(props) {
    const { datosPersonales, setDatosPersonales, onNext, onCLose } = props;

    // Inicializa estados para rastrear errores en cada campo
    const [nombreError, setNombreError] = useState(false);
    const [apellidoError, setApellidoError] = useState(false);
    const [correoError, setCorreoError] = useState(false);
    const [numeroError, setNumeroError] = useState(false);
    const [generoSeleccionado, setGeneroSeleccionado] = useState(false);

    useEffect(() => {
        checkGender();
    }, []);

    const checkGender = () => {
        if (datosPersonales.genero) {
            setGeneroSeleccionado(datosPersonales.genero);
        }
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        setDatosPersonales({ ...datosPersonales, [name]: value });

        if (e.target.value.length > 0) {
            // Validar y actualizar el estado de error para cada campo
            if (name === "nombre") {
                setNombreError(
                    !validarLongitud(value, 2, 20) && !campoEstaVacio(value) && value.trim() !== ""
                );
            } else if (name === "apellido") {
                setApellidoError(
                    !validarLongitud(value, 2, 20) && !campoEstaVacio(value) && value.trim() !== ""
                );
            } else if (name === "correo") {
                setCorreoError(
                    !validarLongitud(value, 2, 40) && !campoEstaVacio(value) && value.trim() !== "" && !emailRegex.test(value)
                );
            } else if (name === "numero") {
                setNumeroError(
                    !validarLongitud(value, 10, 10) && !campoEstaVacio(value) && value.trim() !== ""
                );
            }
        }
    };

    const handleGeneroChange = (e) => {
        const { name, value } = e.target;
        setDatosPersonales({ ...datosPersonales, [name]: value });
        setGeneroSeleccionado(value !== "");
    };

    // Función para validar la longitud de un campo
    const validarLongitud = (valor, min, max) => {
        return valor.length >= min && valor.length <= max;
    };

    // Función para validar si un campo está vacío
    const campoEstaVacio = (valor) => {
        return valor.trim() === '';
    };

    return (
        <div className='row justify-content-center align-items-center'>
            <div className='col-sm-6'>
                <h2>Paso 1: Datos personales</h2>
                <form>
                    <div className="form-group">
                        <label htmlFor="nombre" style={{ fontWeight: "bold" }}>Nombre:</label>
                        <input
                            type="text"
                            id="nombre"
                            name="nombre"
                            className={`form-control ${nombreError || campoEstaVacio(datosPersonales.nombre) ? 'is-invalid' : ''}`}
                            placeholder="Nombre"
                            value={datosPersonales.nombre}
                            onChange={handleChange}
                            required  // Campo obligatorio
                        />
                        {/* Mostrar mensaje de error solo cuando hay un error */}
                        {(nombreError || campoEstaVacio(datosPersonales.nombre)) && datosPersonales.nombre.length > 0 && (
                            <div className="invalid-feedback">El nombre debe tener entre 2 y 20 caracteres.</div>
                        )}
                    </div>
                    <div className="form-group">
                        <label htmlFor="apellido" style={{ fontWeight: "bold" }}>Apellido:</label>
                        <input
                            type="text"
                            id="apellido"
                            name="apellido"
                            className={`form-control ${apellidoError || campoEstaVacio(datosPersonales.apellido) ? 'is-invalid' : ''}`}
                            placeholder="Apellido"
                            value={datosPersonales.apellido}
                            onChange={handleChange}
                            required  // Campo obligatorio
                        />
                        {/* Mostrar mensaje de error solo cuando hay un error */}
                        {(apellidoError || campoEstaVacio(datosPersonales.apellido)) && datosPersonales.apellido.length > 0 && (
                            <div className="invalid-feedback">El apellido debe tener entre 2 y 20 caracteres.</div>
                        )}
                    </div>
                    <div className="form-group">
                        <label htmlFor="correo" style={{ fontWeight: "bold" }}>Correo:</label>
                        <input
                            type="email"
                            id="correo"
                            name="correo"
                            className={`form-control ${correoError || campoEstaVacio(datosPersonales.correo) ? 'is-invalid' : ''}`}
                            placeholder="Correo"
                            value={datosPersonales.correo}
                            onChange={handleChange}
                            required  // Campo obligatorio
                        />
                        {/* Mostrar mensaje de error solo cuando hay un error */}
                        {(correoError || campoEstaVacio(datosPersonales.correo)) && datosPersonales.correo.length > 0 && (
                            <div className="invalid-feedback">El correo debe tener entre 2 y 40 caracteres y tener un formato válido.</div>
                        )}
                    </div>
                    <div className="form-group">
                        <label htmlFor="numero" style={{ fontWeight: "bold" }}>Número:</label>
                        <input
                            type="number"
                            id="numero"
                            name="numero"
                            className={`form-control ${numeroError || campoEstaVacio(datosPersonales.numero) ? 'is-invalid' : ''}`}
                            placeholder="Número"
                            value={datosPersonales.numero}
                            onChange={handleChange}
                            required  // Campo obligatorio
                        />
                        {/* Mostrar mensaje de error solo cuando hay un error */}
                        {(numeroError || campoEstaVacio(datosPersonales.numero)) && datosPersonales.numero.length > 0 && (
                            <div className="invalid-feedback">El número debe tener 10 números.</div>
                        )}
                    </div>
                    <label style={{ fontWeight: "bold" }}>Género:</label> &nbsp; &nbsp;
                    <select
                        className={`main-select2 ${generoSeleccionado ? '' : 'is-invalid'}`}
                        onChange={handleGeneroChange}
                        name="genero"
                        value={datosPersonales.genero}
                        required
                    >
                        <option value="">Seleccione</option>
                        <option value="Masculino">El</option>
                        <option value="Femenino">Ella</option>
                    </select> &nbsp; &nbsp;
                </form>
                <button
                    className={`main-button mt-2 ${nombreError || apellidoError || correoError || numeroError || campoEstaVacio(datosPersonales.nombre) || campoEstaVacio(datosPersonales.apellido) || campoEstaVacio(datosPersonales.correo) || campoEstaVacio(datosPersonales.numero) || !generoSeleccionado ? 'disabled_button_next' : ''}`}
                    disabled={(nombreError || apellidoError || correoError || numeroError || campoEstaVacio(datosPersonales.nombre) || campoEstaVacio(datosPersonales.apellido) || campoEstaVacio(datosPersonales.correo) || campoEstaVacio(datosPersonales.numero) || !generoSeleccionado)}
                    onClick={onNext}
                >
                    Siguiente
                </button>
                {/* Mostrar mensaje general de error si no todos los campos están completos */}
                {
                    !(nombreError || apellidoError || correoError || numeroError || campoEstaVacio(datosPersonales.nombre) || campoEstaVacio(datosPersonales.apellido) || campoEstaVacio(datosPersonales.correo) || campoEstaVacio(datosPersonales.numero)) && (
                        <p className="error mt-2">Llene todos los campos</p>
                    )
                }
            </div>
        </div>
    );
}
