import React, { useContext, useEffect, useState } from 'react';
import api from "../../../api/api";
import Swal from 'sweetalert2';
import Sidebar from '../../Components/Sidebar';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../../Context/AuthContext';

const CreateLocal = ({ closeModal }) => {

    const navigator = useNavigate()

    const [localName, setLocalName] = useState('');
    const [editedSchedules, setEditedSchedules] = useState([]);
    const [employees, setEmployees] = useState()
    const { user } = useContext(AuthContext);

    console.log(user)

    const handleCreateLocal = async (e) => {




        e.preventDefault();

        if (user.permisos?.locales.ver) {

            const completeSchedules = daysOfWeek.map((day, index) => ({
                day,
                opening: editedSchedules[index]?.opening || '',
                closing: editedSchedules[index]?.closing || '',
            }));

            //console.log("sasa", completeSchedules)

            // Validación: Verificar que se hayan ingresado todos los datos obligatorios
            if (!localName | !employees || employees <= 0 || completeSchedules.some(schedule => !schedule.day || !schedule.opening || !schedule.closing)) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                    text: '¡Por favor, ingresa todos los datos obligatorios!',
                    confirmButtonColor: '#e33084',
                    allowOutsideClick: false,
                    showConfirmButton: true,
                });
                return; // Detener la creación si falta algún dato obligatorio
            }

            // Agregar el campo "day" a cada horario en función de daysOfWeek


            const body = {
                local: localName,
                ubication: localName,
                schedules: completeSchedules, // Utilizar los horarios completos con el campo "day"
                employees: employees
            };

            try {
                const response = await api.post('/local', body);

                Swal.fire({
                    icon: 'success',
                    iconHtml: '<img src="https://www.easy-gst.in/wp-content/uploads/2017/07/success-icon-10.png" alt="Icono personalizado" width="98">',
                    title: 'Éxito',
                    text: '¡Se ha creado un nuevo local!',
                    confirmButtonColor: '#e33084',
                    allowOutsideClick: false,
                    showConfirmButton: true,
                });



                // En lugar de cerrar el modal, puedes realizar otras acciones o simplemente limpiar los campos
                setLocalName('');
                setEditedSchedules([]);
                navigator("/local")
            } catch (error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                    text: '¡Ha ocurrido un error al crear el local!',
                    confirmButtonColor: '#e33084',
                    allowOutsideClick: false,
                    showConfirmButton: true,
                });
            }
        } else {

            Swal.fire({
                icon: 'error',
                title: 'Error',
                confirmButtonColor: '#e33084',
                text: '¡Ha ocurrido un error al crear el local!',
                allowOutsideClick: false,
                showConfirmButton: true,
            });

        }


    };



    const handleNameChange = (e) => {
        setLocalName(e.target.value);
    };

    const handleNumber = (e) => {
        setEmployees(e.target.value);
    };

    const handleInputChange = (e, index, field) => {
        const updatedSchedules = [...editedSchedules];
        updatedSchedules[index] = {
            ...updatedSchedules[index],
            [field]: e.target.value,
        };
        setEditedSchedules(updatedSchedules);
    };

    // Definimos los días fijos
    const daysOfWeek = ["lunes", "martes", "miércoles", "jueves", "viernes", "sábado", "domingo"];

    return (
        <>
            <Sidebar />
            <div className='container-fluid'>
                <div className='row justify-content-center'>
                    <div className='col-sm-5'>
                        {user.permisos?.locales.ver ? (

                            <div>
                                <h2>Crear Nuevo Local</h2>
                                <form>
                                    <div className='form-group'>
                                        <label htmlFor='localName'>Nombre del Local:</label>
                                        <input
                                            type='text'
                                            className='form-control'
                                            id='localName'
                                            value={localName}
                                            onChange={handleNameChange}
                                            required
                                        />
                                    </div>

                                    <div className='form-group'>
                                        <label htmlFor='empleados'>Número de Empleados:</label>
                                        <input
                                            type='number'
                                            className='form-control'
                                            id='empleados'
                                            value={employees}
                                            onChange={handleNumber}
                                            required
                                        />
                                    </div>

                                    <button className='main-button' type="button" onClick={handleCreateLocal}>Crear Local</button>
                                    &nbsp; &nbsp;
                                    <Link to="/local"><button className='main-close' type="button">Regresar</button></Link>
                                    <table className="table">
                                        <thead>
                                            <tr>
                                                <th>Día</th>
                                                <th>Apertura</th>
                                                <th>Cierre</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {daysOfWeek.map((day, index) => (
                                                <tr key={index}>
                                                    <td>
                                                        <input
                                                            type='text'
                                                            className='form-control'
                                                            value={day}
                                                            readOnly // Agregar el atributo readOnly para hacerlo no editable
                                                        />
                                                    </td>
                                                    <td>
                                                        <input
                                                            type='text'
                                                            className='form-control'
                                                            value={editedSchedules[index]?.opening || ''}
                                                            onChange={(e) => handleInputChange(e, index, 'opening')}
                                                            required
                                                        />
                                                    </td>
                                                    <td>
                                                        <input
                                                            type='text'
                                                            className='form-control'
                                                            value={editedSchedules[index]?.closing || ''}
                                                            onChange={(e) => handleInputChange(e, index, 'closing')}
                                                            required
                                                        />
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </form>
                            </div>
                        ) : (<div>No tienes permisos</div >)}

                    </div>
                </div>
            </div>

        </>


    );
};

export default CreateLocal;
