import React, { useState, useEffect, useContext } from 'react';
import Sidebar from '../../Components/Sidebar';
import api from '../../../api/api';
import Swal from 'sweetalert2';
import { Link, useNavigate, useParams } from 'react-router-dom';
import "./Styles.css"
import { AuthContext } from '../../../Context/AuthContext';


export default function EditServiceData() {
    const { user } = useContext(AuthContext);
    const navigator = useNavigate();
    const { id } = useParams(); // Obtener el ID del servicio de los parámetros de la URL

    const [formData, setFormData] = useState({
        name: '',
        duration: '',
        gender: '',
        type: '',
    });

    useEffect(() => {
        // Cargar los datos del servicio existente para la edición
        const fetchServiceData = async () => {
            try {
                const response = await api.get(`/service/${id}`);
                console.log("response", response)
                if (response.status === 200) {
                    const serviceData = response.data.service; // Supongamos que los datos vienen en un objeto con las mismas claves que en el formulario
                    setFormData({
                        ...formData,
                        name: serviceData.name,
                        duration: serviceData.duration,
                        gender: serviceData.gender,
                        type: serviceData.type,
                    });
                }
            } catch (error) {
                console.error('Error al cargar los datos del servicio:', error);
            }
        };

        fetchServiceData();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (user.permisos?.servicios.editar) {

            try {
                const response = await api.put(`/service/${id}`, formData); // Utilizar una solicitud PUT para actualizar el servicio
                if (response.status === 200) {

                    console.log("!!!!!!!!!!!!!!!!!")
                    // Servicio actualizado exitosamente
                    Swal.fire({
                        icon: 'success',
                        iconHtml: '<img src="https://www.easy-gst.in/wp-content/uploads/2017/07/success-icon-10.png" alt="Icono personalizado" width="98">',
                        title: '¡Servicio actualizado exitosamente!',
                        showConfirmButton: false,
                        timer: 1500,
                    });

                    setTimeout(() => {
                        navigator('/servicios');
                    }, 1500);
                }
            } catch (error) {
                console.error('Error al actualizar el servicio:', error);
                Swal.fire({
                    icon: 'error',
                    iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                    title: 'Error al actualizar el servicio',
                    text: 'Ha ocurrido un error al actualizar el servicio.',
                });
            }
        } else {
            Swal.fire({
                icon: 'error',
                iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                title: '!No tienes permisos¡',
                text: 'Ha ocurrido un error al actualizar el servicio.',
            });
        }

    };

    return (
        <>
            <Sidebar />
            <div className="container-fluid mt-5">
                <div className="row justify-content-center">
                    <div className="col-sm-8">
                        {user.permisos?.servicios.ver ? (
                            <div>
                                <h2>Editar Servicio</h2>
                                <form onSubmit={handleSubmit}>
                                    <div className="form-group">
                                        <label>Nombre del Servicio:</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Duración (en minutos):</label>
                                        <input
                                            type="number"
                                            className="form-control"
                                            name="duration"
                                            value={formData.duration}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Género:</label>
                                        <select
                                            className="form-control"
                                            name="gender"
                                            value={formData.gender} // Convierte a minúsculas
                                            onChange={handleChange}
                                            required
                                        >
                                            <option value="Masculino">Masculino</option>
                                            <option value="Femenino">Femenino</option>
                                        </select>
                                    </div>

                                    <div className="form-group">
                                        <label>Tipo:</label>
                                        <select
                                            className="form-control"
                                            name="type"
                                            value={formData.type}
                                            onChange={handleChange}
                                            required
                                        >
                                            <option value="Corporal">Corporal</option>
                                            <option value="Facial">Facial</option>
                                            <option value="Bronceado">Bronceado</option>
                                        </select>
                                    </div>
                                    <button type="submit" className="main-button">Actualizar Servicio</button> &nbsp; &nbsp;
                                    <Link to="/servicios"><button type="submit" className="main-close">Regresar</button></Link>
                                </form>
                            </div>
                        ) : (<div className='col-sm-5 mt-5'><p>No tienes Permisos</p></div>)}

                    </div>
                </div>
            </div>
        </>
    );
}
