import React, { useState } from 'react';
import "./home.css"
import { useNavigate } from 'react-router-dom';
import avatar from "../Images/avataaars.svg"
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Input from '@mui/material/Input';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Form from 'react-bootstrap/Form';

function Home() {


    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí puedes agregar la lógica de autenticación si es necesario
    };


    const handleClickShowPassword = () => {
        setShowPassword(!showPassword);
    };

    const handleMouseDownPassword = (event) => {
        event.preventDefault();
    };

    return (
        <div className='container-fluid background'>


            <div className='row justify-content-center'>

                <div className='col-sm'>

                    <img src={avatar} className='img-fluid avatar' />

                    <h1 className='title'>Bienvenidos</h1>

                    <Form className="Auth-form">
                        <div className="Auth-form-content">
                            <h3 className="Auth-form-title">Ingresar</h3>
                            <FormControl variant="standard" sx={{ m: 1, width: '100%' }}>
                                <InputLabel className="label-auth" htmlFor="input-with-icon-adornment">
                                    Correo electrónico
                                </InputLabel>
                                <Input
                                    id="input-with-icon-adornment"
                                    type="email"
                                    startAdornment={
                                        <InputAdornment position="start">
                                            <AccountCircle />
                                        </InputAdornment>
                                    }
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </FormControl>

                            <FormControl sx={{ m: 1, width: '100%' }} variant="standard">
                                <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
                                <Input
                                    id="outlined-adornment-password"
                                    type={showPassword ? 'text' : 'password'}
                                    onChange={(e) => setPassword(e.target.value)}
                                    startAdornment={
                                        <InputAdornment position="start">
                                            <KeyIcon />
                                        </InputAdornment>
                                    }
                                    endAdornment={
                                        <InputAdornment position="end">
                                            <IconButton
                                                aria-label="toggle password visibility"
                                                onClick={handleClickShowPassword}
                                                onMouseDown={handleMouseDownPassword}
                                                edge="end"
                                            >
                                                {showPassword ? <VisibilityOff /> : <Visibility />}
                                            </IconButton>
                                        </InputAdornment>
                                    }
                                    label="Password"
                                />
                            </FormControl>

                            <div className="d-grid gap-2 mt-3">
                                <button onClick={authSubmitHandler} className="main-button">
                                    Ingresar
                                </button>
                            </div>

                            <p className="forgot-password text-right mt-2">
                                ¿Olvidaste tu <a href="#">contraseña?</a>
                            </p>
                        </div>
                    </Form >



                </div>

            </div>



        </div>
    );
}

export default Home;
