import React, { useEffect, useState } from 'react'
import api from "../../api/api"
import Swal from 'sweetalert2';
//import { useNavigate } from 'react-router-dom';



export default function Resume(props) {


    const { servicios, info, datosPersonales, onPrev, onCLose, duracion, setModalIsOpen, hora, minuto, horaFinal, localId, setHoraFinal, employee } = props;

    const [local, setLocal] = useState('')








    useEffect(() => {

        getLocalById()



    }, [])






    const getLocalById = async () => {

        try {



            const response = await api.get(`/local/${localId}`)

            setLocal(response.data.local.local)

        } catch (error) {
            console.log("error", error)
        }
    }






    async function onBooking() {

        //  console.log("entree al booking")

        console.log("prueba", localId)

        const body = {
            nombre: datosPersonales.nombre,
            apellido: datosPersonales.apellido,
            correo: datosPersonales.correo,
            numero: datosPersonales.numero,
            genero: datosPersonales.genero,

            empleado: employee,
            servicios: servicios,
            duracion: duracion + 5,
            local_id: localId,
            fecha: info.fecha,
            hora: hora,
            hora_fin: horaFinal
        }
        console.log(body);

        console.log("hora", body)



        try {


            Swal.fire({
                position: 'center',
                title: 'Espera un momento...',
                showConfirmButton: false,
                allowOutsideClick: false,
                html: '<div style="color: #e33084; overflow-y: hidden;"><i class="fas fa-circle-notch fa-spin fa-3x"></i></div>',
            })


            const response = await api.post("/booking", body)



            if (response.data.status) {

                Swal.close()
                Swal.fire({
                    icon: 'success',
                    confirmButtonColor: '#e33084',
                    iconHtml: '<img src="https://www.easy-gst.in/wp-content/uploads/2017/07/success-icon-10.png" alt="Icono personalizado" width="98">', title: 'Agenda',
                    html: `¡Te enviaremos un correo!<br>Se ha agendado una cita en el local:<br><strong>${local}
                    </strong><br>Fecha: <strong>${info.fecha}</strong><br>Hora: 
                    <strong>${hora}</strong>`,
                    allowOutsideClick: false,
                    showConfirmButton: true,
                }).then((result) => {
                    // Este bloque se ejecuta después de que el usuario confirme la alerta
                    if (result.isConfirmed) {
                        // Aquí puedes realizar acciones adicionales o redirigir a otra página si es necesario
                        onCLose()

                    }
                });



            }





        } catch (error) {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: "Ha ocurrido un error",
                confirmButtonColor: '#e33084',
                iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
            });
        }


    }


    return (

        <div>

            <h2>Resumen</h2>

            <div className='row justify-content-center mt-3'>

                <div className='col-sm-5'>

                    <h5>Servicios seleccionados</h5>

                    <table className="table">
                        <thead>
                            <tr>
                                <th>Servicio</th>
                                <th>Duración</th>
                            </tr>
                        </thead>
                        <tbody>
                            {servicios.map((servicio, index) => (
                                <tr key={index}>
                                    <td>{servicio.name}</td>
                                    <td>{servicio.duration} minutos</td>

                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <p>Duración: {duracion + 5} minutos</p>


                </div>

                <div className='col-sm-5'>

                    <h5>Local, hora y fecha</h5>

                    <form>
                        <div className="form-group">
                            <label htmlFor="nombre">Local:</label>
                            <input
                                type="text"
                                id="local"
                                name="local"
                                className="form-control"
                                placeholder="local"
                                value={local}
                                readOnly // Hace que el campo sea de solo lectura
                                style={{ backgroundColor: '#f2f2f2' }} // Cambia el fondo a gris claro
                            />

                        </div>
                        <div className="form-group">
                            <label htmlFor="correo">Fecha:</label>
                            <input
                                type="text"
                                id="fecha"
                                name="fecha"
                                className="form-control"
                                placeholder="fecha"
                                value={info.fecha}
                                readOnly // Hace que el campo sea de solo lectura
                                style={{ backgroundColor: '#f2f2f2' }} // Cambia el fondo a gris claro
                            />
                            {/* Agrega un mensaje de error si el correo no cumple con la longitud requerida */}

                        </div>
                        <div className="form-group">
                            <label htmlFor="numero">Hora</label>
                            <input
                                type="text"
                                id="hora"
                                name="hora"
                                className="form-control"
                                placeholder="hora"
                                value={hora}
                                readOnly // Hace que el campo sea de solo lectura
                                style={{ backgroundColor: '#f2f2f2' }} // Cambia el fondo a gris claro
                            />

                        </div>

                    </form>


                </div>



            </div>

            <div className='row justify-content-center'>

                <div className='col-sm-5'>

                    <button className="main-button mt-2" onClick={onPrev}>Anterior</button> &nbsp; &nbsp;

                    <button className="main-close mt-2" onClick={() => { onBooking(); }}>Reservar</button>






                </div>

            </div>




            <div>

            </div>

        </div >

    )
}
