import React, { useState, useEffect } from 'react';
import './Styles.css';
import api from '../../api/api';
import { AiOutlineCheck } from "react-icons/ai";

function SecondStep(props) {
    const {
        servicios,
        setServicios,
        duracion,
        setDuracion,
        onNext,
        onPrev,
        onCLose,
        gender
    } = props;

    const [servicesData, setServicesData] = useState([]);
    const [categoriaSeleccionada, setCategoriaSeleccionada] = useState("Facial");
    const [duracionTotal, setDuracionTotal] = useState(0);
    const [serviciosSeleccionados, setServiciosSeleccionados] = useState([]);
    const [corporal, setCorporal] = useState([]);
    const [facial, setFacial] = useState([]);
    const [brocenado, setBronceado] = useState([]);

    useEffect(() => {
        getServices();
    }, [gender]);

    const getServices = async () => {
        try {
            const response = await api.get('/service');
            setServicesData(response.data.services);

            const serviciosFacial = response.data.services
                .filter((service) => service.gender === gender && service.type === "Facial")
                .sort((a, b) => a.name.localeCompare(b.name));

            const serviciosCorporal = response.data.services
                .filter((service) => service.gender === gender && service.type === "Corporal")
                .sort((a, b) => a.name.localeCompare(b.name));

            const serviciosBronceado = response.data.services
                .filter((service) => service.gender === gender && service.type === "Bronceado")
                .sort((a, b) => a.name.localeCompare(b.name));

            if (servicios.length > 0) {
                const duracionTotal = servicios.reduce((total, service) => total + service.duration, 0);
                setDuracionTotal(duracionTotal);
            }

            setCorporal(serviciosCorporal);
            setFacial(serviciosFacial);
            setBronceado(serviciosBronceado);
        } catch (error) {
            console.log(error);
        }
    };

    const handleCategoriaChange = (e) => {
        const nuevaCategoria = e.target.value;
        setCategoriaSeleccionada(nuevaCategoria);
    };

    const handleAgregarServicio = (servicio) => {
        if (servicio) {
            // Si el servicio ya está seleccionado, quitarlo de la lista
            if (serviciosSeleccionados.includes(servicio)) {
                setServiciosSeleccionados(serviciosSeleccionados.filter((s) => s !== servicio));
                setServicios(serviciosSeleccionados);
            } else {
                // Agregar el servicio a la lista de servicios seleccionados
                setServiciosSeleccionados([...serviciosSeleccionados, servicio]);
                setServicios(serviciosSeleccionados);
            }
        }
    };

    // Actualiza la duración total en duracion
    useEffect(() => {
        const nuevaDuracionTotal = serviciosSeleccionados.reduce((total, service) => total + service.duration, 0);
        setDuracionTotal(nuevaDuracionTotal);
        setDuracion(nuevaDuracionTotal);
        setServicios(serviciosSeleccionados);
    }, [serviciosSeleccionados]);

    return (
        <div className="row justify-content-center">
            <div className="col-sm-6">
                <h2>Paso 2: Servicios</h2>
                <div>
                    <h5>Selecciona una categoría:</h5>
                    <div>
                        <label>
                            <input
                                type="radio"
                                name="categoria"
                                value="Facial"
                                checked={categoriaSeleccionada === "Facial"}
                                onChange={handleCategoriaChange}
                            />
                            &nbsp;
                            Depilación Facial
                        </label> &nbsp; &nbsp;
                        <label>
                            <input
                                type="radio"
                                name="categoria"
                                value="Corporal"
                                checked={categoriaSeleccionada === "Corporal"}
                                onChange={handleCategoriaChange}
                            />
                            &nbsp;
                            Depilación Corporal
                        </label> &nbsp; &nbsp;
                        <label>
                            <input
                                type="radio"
                                name="categoria"
                                value="Bronceado"
                                checked={categoriaSeleccionada === "Bronceado"}
                                onChange={handleCategoriaChange}
                            />
                            &nbsp;
                            Bronceado
                        </label>
                    </div>
                    <label htmlFor="categoria">Seleccionar servicio:</label>
                    <div className='mt-2 form-group'>
                        <div >
                            <div className="scrollable-table">
                                <table className="table">
                                    <tbody>
                                        {categoriaSeleccionada === 'Facial' && facial.map((servicio) => (
                                            <tr key={servicio._id}>
                                                <td className={`main-close3 ${serviciosSeleccionados.includes(servicio) ? 'selected' : ''}`}
                                                    onClick={() => handleAgregarServicio(servicio)}>
                                                    {servicio.name}
                                                    {" "}
                                                    {serviciosSeleccionados && serviciosSeleccionados.includes(servicio) && <AiOutlineCheck style={{ color: 'green' }} />}
                                                </td>
                                            </tr>
                                        ))}
                                        {categoriaSeleccionada === 'Corporal' && corporal.map((servicio) => (
                                            <tr key={servicio._id}>
                                                <td className={`main-close3 ${serviciosSeleccionados.includes(servicio) ? 'selected' : ''}`}
                                                    onClick={() => handleAgregarServicio(servicio)}
                                                >
                                                    {servicio.name}
                                                    {" "}
                                                    {serviciosSeleccionados && serviciosSeleccionados.includes(servicio) && <AiOutlineCheck style={{ color: 'green' }} />}
                                                </td>
                                            </tr>
                                        ))}
                                        {categoriaSeleccionada === 'Bronceado' && brocenado.map((servicio) => (
                                            <tr key={servicio._id}>
                                                <td
                                                    className={`main-close3 ${serviciosSeleccionados.includes(servicio) ? 'selected' : ''}`}
                                                    onClick={() => handleAgregarServicio(servicio)}
                                                >
                                                    {servicio.name}
                                                    {" "}
                                                    {serviciosSeleccionados && serviciosSeleccionados.includes(servicio) && <AiOutlineCheck style={{ color: 'green' }} />}
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                            <h5>Servicios seleccionados</h5>
                            <div className="scrollable-table">
                                <table className="table">
                                    <thead>
                                        <tr>
                                            <th>Nombre</th>
                                            <th>Duración</th>
                                            <th>Acción</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {serviciosSeleccionados.map((servicio) => (
                                            <tr key={servicio._id}>
                                                <td>{servicio.name}</td>
                                                <td>{servicio.duration} minutos</td>
                                                <td>
                                                    <button
                                                        className="main-close2"
                                                        onClick={() => handleAgregarServicio(servicio)}
                                                    >
                                                        {serviciosSeleccionados.includes(servicio) ? 'Eliminar' : 'Agregar'}
                                                    </button>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
                {serviciosSeleccionados.length === 0 && (
                    <p className="text-danger mt-2">Por favor, selecciona un servicio.</p>
                )}
                <p>Duración total: {duracionTotal + 5} minutos</p>
                <p>Se añaden 5 minutos por protocolo</p>
                <button className="main-button mt-2" onClick={() => { onPrev(); }}>
                    Anterior
                </button>
                &nbsp; &nbsp;
                <button
                    className="main-close mt-2"
                    onClick={onNext}
                    disabled={serviciosSeleccionados.length === 0}
                >
                    Siguiente
                </button>
            </div>
        </div>
    );
}

export default SecondStep;
