import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import Sidebar from '../../Components/Sidebar';
import api from '../../../api/api';
import EditLocal from './EditLocal';
import CreateLocal from './CreateLocal'; // Importa el componente CreateLocal
import "./Styles.css"
import { AuthContext } from '../../../Context/AuthContext';
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

export default function Local() {
    const navigator = useNavigate()
    const [localesDisponibles, setLocalesDisponibles] = useState([]);
    const [localSeleccionado, setLocalSeleccionado] = useState([]);
    const [modalIsOpen, setModalIsOpen] = useState(false); // Estado para controlar si el modal de edición está abierto
    const [createModalIsOpen, setCreateModalIsOpen] = useState(false); // Estado para controlar si el modal de creación está abierto
    const [localId, setLocalId] = useState('');
    const [employe, setEmploye] = useState()
    const [error, setError] = useState('');


    const { user } = useContext(AuthContext);

    console.log("user", user.permisos?.locales.editar)

    const handleEmployeChange = (e) => {
        const newValue = e.target.value;
        if (newValue >= 0) {
            setEmploye(newValue);
            setError(''); // Borra el mensaje de error si el valor es válido
        } else {
            setEmploye('');
            setError('El número de empleados debe ser positivo o cero.');
        }
    }


    const handleEliminateLocal = async (id) => {

        if (user.permisos?.locales.eliminar) {

            Swal.fire({
                title: '¿Estás seguro?',
                text: 'Esta acción eliminará el local permanentemente.',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'Sí, eliminar',
                cancelButtonText: 'Cancelar',
            }).then(async (result) => {
                if (result.isConfirmed) {
                    try {
                        const response = await api.delete(`/local/${id}`);
                        // Si la eliminación se realiza con éxito, puedes mostrar un mensaje de éxito
                        Swal.fire({
                            icon: 'success',
                            confirmButtonColor: '#e33084',
                            iconHtml: '<img src="https://www.easy-gst.in/wp-content/uploads/2017/07/success-icon-10.png" alt="Icono personalizado" width="98">',
                            title: 'Éxito',
                            text: '¡El local se ha eliminado correctamente!',
                            allowOutsideClick: false,
                            showConfirmButton: true,
                        });

                        window.location.reload();

                        // Aquí puedes realizar cualquier otra acción después de la eliminación si es necesario
                    } catch (error) {
                        // Si ocurre un error en la eliminación, muestra un mensaje de error
                        Swal.fire({
                            icon: 'error',
                            title: 'Error',
                            confirmButtonColor: '#e33084',
                            iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                            text: '¡Ha ocurrido un error al eliminar el local!',
                            allowOutsideClick: false,
                            showConfirmButton: true,
                        });
                    }
                }
            });

        } else {

            Swal.fire({
                icon: 'error',
                confirmButtonColor: '#e33084',
                iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                title: 'Error',
                text: '¡No tienes permisos!',
                allowOutsideClick: false,
                showConfirmButton: true,
            });
        }
        // Mostrar un cuadro de diálogo de confirmación

    }



    useEffect(() => {
        // Obtener la lista de locales y sus horarios disponibles desde la API
        async function fetchLocales() {
            try {
                const response = await api.get('/local');
                if (response.status === 200) {
                    setLocalesDisponibles(response.data.locals);
                }
            } catch (error) {
                console.error('Error al obtener la lista de locales:', error);
            }
        }

        // Llamar a la función para obtener locales al cargar el componente
        fetchLocales();
    }, []);

    return (
        <>
            <Sidebar />
            <div className="container">
                <div className="row justify-content-center align-items-center">

                    {user.permisos?.locales.ver ? (
                        <div className="col-sm mt-5">
                            <label>Selecciona un Local:</label>
                            <select
                                value={localId}
                                onChange={(e) => {
                                    const selectedLocalId = e.target.value;
                                    setLocalId(selectedLocalId);
                                }}
                                className="form-control"
                            >
                                <option value="">Seleccionar local</option>
                                {

                                    localesDisponibles.map((local, index) => (
                                        < option key={index} value={local._id} >
                                            {local.local}
                                        </option>
                                    ))}
                            </select>







                            {localesDisponibles.map((local) => {
                                if (local._id === localId) {
                                    return (
                                        <div className='form-group' key={local._id}>
                                            <label htmlFor='localName'>Número de Empleados</label>
                                            <input
                                                type='text'
                                                className='form-control'
                                                id='localName'
                                                value={local.employees}
                                                readOnly
                                            />
                                        </div>
                                    );
                                }
                                return null; // Retorna null para otros locales que no coincidan
                            })}


                            <button className='main-button mt-2'
                                onClick={() => {
                                    if (!user.permisos?.locales.editar) {
                                        Swal.fire({
                                            icon: 'error',
                                            confirmButtonColor: '#e33084',
                                            iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                                            title: 'Error',
                                            text: '¡No tienes permisos!',
                                            allowOutsideClick: false,
                                            showConfirmButton: true,
                                        });
                                    } else {
                                        console.log("entre al else")
                                        navigator("/createLocal")
                                    }

                                }}
                            >

                                Crear Local
                            </button>


                            &nbsp; &nbsp;

                            <button
                                className={`main-close-btn  ${!localId && localId !== '' ? 'disabled-button' : ''}`}
                                disabled={!localId && user.permisos?.locales.eliminar}
                                onClick={() => handleEliminateLocal(localId)}
                            >
                                Eliminar Local
                            </button> &nbsp; &nbsp;




                            <button
                                className={`main-button mt-2 ${!localId && localId !== '' ? 'disabled-button' : ''}`}
                                disabled={!localId}

                                onClick={() => {
                                    if (!user.permisos?.locales.editar) {


                                        Swal.fire({
                                            icon: 'error',
                                            confirmButtonColor: '#e33084',
                                            iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                                            title: 'Error',
                                            text: '¡No tienes permisos!',
                                            allowOutsideClick: false,
                                            showConfirmButton: true,
                                        });
                                    } else {
                                        console.log("entre al else de editar")
                                        navigator(`/editLocal/${localId}`)
                                    }

                                }}
                            >

                                Editar Local
                            </button>





                            <div className="mt-5">
                                <table className="table">
                                    <thead>
                                        <tr>
                                            <th>Día</th>
                                            <th>Hora de Apertura</th>
                                            <th>Hora de Cierre</th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {user.permisos?.locales.ver ? (<>
                                            {
                                                localesDisponibles
                                                    .find((local) => local._id === localId)
                                                    ?.schedules?.map((horario, index) => (
                                                        <tr key={index}>
                                                            <td>{horario.day}</td>
                                                            <td>{horario.opening}</td>
                                                            <td>{horario.closing}</td>
                                                        </tr>
                                                    ))
                                            }
                                        </>) : (
                                            <tr >
                                                <td colSpan={3}>No tienes permisos</td>
                                            </tr>)}
                                    </tbody>
                                </table>
                            </div>
                        </div>

                    ) : (<div className='col-sm-5 mt-5'><p>No tienes Permisos</p></div>)}

                </div>
            </div >
        </>
    );
}
