import React, { useContext, useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import Modal from 'react-modal';
import api from "../../../api/api";
import Swal from 'sweetalert2';
import Sidebar from '../../Components/Sidebar';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../../Context/AuthContext';

const EditLocal = () => {

    const { user } = useContext(AuthContext);

    const navigator = useNavigate();


    const { id } = useParams(); // Obtener el parámetro "id" de la URL
    const [local, setLocal] = useState({});
    const [localName, setLocalName] = useState('');
    const [schedules, setSchedules] = useState([]);
    const [editedSchedules, setEditedSchedules] = useState([]);
    const [employe, setEmploye] = useState(1);
    const [error, setError] = useState('');

    useEffect(() => {

        getLocal();
    }, []);

    const handleEmployeChange = (e) => {
        const newValue = e.target.value;
        if (newValue >= 0) {
            setEmploye(newValue);
            setError(''); // Borra el mensaje de error si el valor es válido
        } else {
            setEmploye('');
            setError('El número de empleados debe ser positivo o cero.');
        }
    };

    const handleEditLocal = async (e) => {

        e.preventDefault();

        if (user.permisos?.locales.editar) {

            const body = {
                local: localName,
                ubication: localName,
                schedules: editedSchedules,
                employe: employe
            };

            try {
                const response = await api.put(`/local/${id}`, body);

                Swal.fire({
                    icon: 'success',
                    confirmButtonColor: '#e33084',
                    iconHtml: '<img src="https://www.easy-gst.in/wp-content/uploads/2017/07/success-icon-10.png" alt="Icono personalizado" width="98">',
                    title: 'Éxito',
                    text: '¡Se ha editado el local!',
                    allowOutsideClick: false,
                    showConfirmButton: true,
                });

                navigator("/local")


            } catch (error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: '¡Ha ocurrido un error!',
                    confirmButtonColor: '#e33084',
                    allowOutsideClick: false,
                    showConfirmButton: true,
                });
            }

        } else {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                confirmButtonColor: '#e33084',
                iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
                text: '¡No tienes Permisos!',
                allowOutsideClick: false,
                showConfirmButton: true,
            });

        }







    }

    const handleNameChange = (e) => {
        setLocalName(e.target.value);
    };

    const handleOpeningChange = (e, index) => {
        const updatedSchedules = [...editedSchedules];
        updatedSchedules[index] = { ...updatedSchedules[index], opening: e.target.value };
        setEditedSchedules(updatedSchedules);
    };

    const handleClosingChange = (e, index) => {
        const updatedSchedules = [...editedSchedules];
        updatedSchedules[index] = { ...updatedSchedules[index], closing: e.target.value };
        setEditedSchedules(updatedSchedules);
    };

    const getLocal = async () => {
        try {
            const response = await api.get(`/local/${id}`);
            setLocal(response.data.local);
            setLocalName(response.data.local.local);
            setSchedules(response.data.local.schedules);
            setEmploye(response.data.local.employees)

            // Inicializar el estado de edición con los valores actuales
            setEditedSchedules(response.data.local.schedules.map(schedule => ({ ...schedule })));
        } catch (error) {
            console.log("error", error);
        }
    }

    return (
        <>
            <Sidebar />

            <div className='container-fluid mt-3'>
                <div className='row justify-content-center'>
                    <div className='col-sm-10'>

                        {user.permisos?.locales.ver ? (
                            <div>
                                <h2>Editar Local</h2>

                                <form onSubmit={handleEditLocal}>
                                    <div className='form-group'>
                                        <label htmlFor='localName'>Nombre del Local:</label>
                                        <input
                                            type='text'
                                            className='form-control'
                                            id='localName'
                                            value={localName}
                                            onChange={handleNameChange}
                                        />
                                    </div>

                                    <div className='form-group'>
                                        <label htmlFor='employe'>Número de empleados:</label>
                                        <input
                                            type='number'
                                            className='form-control'
                                            id='employe'
                                            value={employe}
                                            onChange={handleEmployeChange}
                                        />
                                        {error && <p className='error-message'>{error}</p>}
                                    </div>

                                    <button type="submit" className='main-button mt-3'>Editar Local</button>

                                    &nbsp; &nbsp;
                                    <Link to="/local"><button className='main-close mt-3'>Regresar</button></Link>
                                    <table className="table">
                                        <thead>
                                            <tr>
                                                <th>Día</th>
                                                <th>Apertura</th>
                                                <th>Cierre</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {schedules.map((day, index) => (
                                                <tr key={index}>
                                                    <td className='mt-3'>{day.day}</td>
                                                    <td>
                                                        <input
                                                            type='text'
                                                            className='form-control small-input'
                                                            value={editedSchedules[index].opening}
                                                            onChange={(e) => handleOpeningChange(e, index)}
                                                        />
                                                    </td>
                                                    <td>
                                                        <input
                                                            type='text'
                                                            className='form-control small-input'
                                                            value={editedSchedules[index].closing}
                                                            onChange={(e) => handleClosingChange(e, index)}
                                                        />
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </form>
                            </div>


                        ) : (<div className='col-sm-5 mt-5'><p>No tienes Permisos</p></div>)}



                    </div>
                </div>
            </div>
        </>
    );
};

export default EditLocal;
