import React, { useEffect, useState } from 'react';
import Sidebar from '../../Components/Sidebar';
import api from '../../../api/api';
import '../HomeBackOffice.css';
import Swal from 'sweetalert2';

const LocalBookingAdmin = () => {
  const [selectedLocalId, setSelectedLocalId] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [locales, setLocales] = useState([]);
  const [horario, setHorario] = useState([]);
  const [opcionSeleccionada, setOpcionSeleccionada] = useState(null);

  useEffect(() => {
    // Obtener la lista de locales disponibles desde la API cuando el componente se monta
    async function fetchLocales() {
      try {
        const response = await api.get("/local");
        if (response.status === 200) {
          setLocales(response.data.locals);
        }
      } catch (error) {
        console.error("Error al obtener la lista de locales:", error);
      }
    }

    fetchLocales();
  }, []);

  const fetchHorasDisponibles = async () => {
    if (selectedLocalId && selectedDate) {
      const body = {
        fechaSeleccionada: selectedDate,
      };
      try {
        const response = await api.post("/local/schedule/" + selectedLocalId, body);
        if (response.status === 200) {
          setHorario(response.data.horario);
        }
      } catch (error) {
        console.error("Error al obtener las horas disponibles:", error);
      }
    }
  }
  useEffect(() => {
    fetchHorasDisponibles();
  }, [selectedLocalId, selectedDate]);

  const handleLocalChange = (event) => {
    setSelectedLocalId(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const cambiarEstado = async (index) => {
    setOpcionSeleccionada(index);

    // Verifica si se ha seleccionado una hora válida
    if (selectedLocalId && selectedDate && horario[index]) {
      try {
        const body = {
          localId: selectedLocalId,
          date: selectedDate,
          hour: horario[index].hora,
          estado: horario[index].estado
        };

        // Realiza una solicitud para cambiar el estado de la hora
        const response = await api.post("/local/changeState", body);

        if (response.status === 201 || response.status === 200)  {
          fetchHorasDisponibles();
          Swal.fire({
            icon: 'success',
            iconHtml: '<img src="https://www.easy-gst.in/wp-content/uploads/2017/07/success-icon-10.png" alt="Icono personalizado" width="98">',
            title: 'Estado actualizado',
            showConfirmButton: false,
            timer: 1500, // Cierra automáticamente después de 1.5 segundos
          });
        } else {
          // Muestra una notificación de error con SweetAlert2
          Swal.fire({
            icon: 'error',
            title: 'Error al actualizar el estado',
            text: response.data.error,
            iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
          });
        }
      } catch (error) {
        // Muestra una notificación de error con SweetAlert2 en caso de error
        Swal.fire({
          icon: 'error',
          title: 'Error en la solicitud',
          iconHtml: '<img src="https://cdn.icon-icons.com/icons2/317/PNG/512/sign-error-icon_34362.png" width="125">',
          text: 'Ocurrió un error al realizar la solicitud.',
        });
        console.error('Error en cambiarEstado:', error);
      }
    }
  };

  return (
    <>
      <Sidebar />
      <div className="container mt-4">
        <h1>Administrar Horario</h1>
        <div className="text-right">
          <button className="btn btn-primary">Volver</button>
        </div>
        <form>
          <div className="form-group">
            <label>Seleccionar Local:</label>
            <select className="form-control" onChange={handleLocalChange}>
              <option value="">Seleccionar</option>
              {locales.map(local => (
                <option value={local._id} key={local._id}>{local.local}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Seleccionar Fecha:</label>
            <input
              type="date"
              className="form-control"
              onChange={handleDateChange}
            />
          </div>
          <div className="form-group">
            <label>Horario:</label>
            <div className="scrollable-table">
              <table className="table">
                <tbody>
                  {horario.map((item, index) => (
                    <tr
                      key={index}
                      className={item.estado === "Ocupado" ? 'opcion-ocupada-hora' : ''}
                    >
                      <td>
                        {item.hora}
                      </td>
                      <td>
                        <button type="button" className={`btn ${item.estado === "Ocupado" ? 'btn-danger' : 'btn-success'}`} onClick={() => cambiarEstado(index)}>
                          {item.estado === "Ocupado" ? 'Ocupado' : 'Disponible'}
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </form>

      </div>
    </>
  );
};

export default LocalBookingAdmin;
