import React, { useState } from 'react';
import Modal from 'react-modal';
import FirstStep from './Steps/FirstStep';
import SecondStep from './Steps/SecondStep';
import ThirdStep from './Steps/ThirdStep'; // Importa el componente del tercer paso
import "./Styles.css"
import Logo from "../Images/workoutLLogo.png"
import Resume from "./Steps/Resume"
import Header from './Components/Header';
import { useNavigate } from 'react-router-dom';
import Footer from './Components/Footer';


function Home() {
    const navigate = useNavigate();
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [pasoActual, setPasoActual] = useState(1);
    const [hora, setHora] = useState('')
    const [minuto, setMinuto] = useState('')
    const [datosPersonales, setDatosPersonales] = useState({
        nombre: '',
        correo: '',
        numero: '',
        genero: '',
    });
    const [servicios, setServicios] = useState([]);
    const [duracion, setDuracion] = useState(0)
    const [horaFinal, setHoraFinal] = useState('')

    const [localId, setlocalId] = useState('')

    const [info, setInfo] = useState({
        local: '',
        local_id: '',
        fecha: '',
        hora: ''
    })

    const openModal = () => {
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
        setPasoActual(1);
        setDatosPersonales({
            nombre: '',
            correo: '',
            numero: '',
            genero: ''
        });
        setServicios([]);
        // Reiniciar los datos del tercer paso
        setDuracion(0)

        setInfo({
            local: '',
            fecha: '',
            hora: '',
            local_id: '',
        });

        setPasoActual(1)
        navigate('/');




    };

    const handleSiguiente = () => {
        setPasoActual(pasoActual + 1);
    };

    const handleAnterior = () => {
        setPasoActual(pasoActual - 1);
    };

    return (
        <>

            <Header />

            <div style={{ paddingTop: '60px' }}> {/* Añade espacio para el header fijo */}

                <div className='container-fluid d-flex justify-content-center align-items-center' style={{ minHeight: '100vh' }}>
                    <div className='row justify-content-center align-items-center'>
                        <div className='col-sm-6'>
                            <h1>Reserva</h1>
                            <p>Realiza una reserva pulsado el botón que aparece a continuación</p>
                            <a href="/reserve"><button className='main-button' onClick={openModal}>Haz una reserva</button></a>

                        </div>
                        <div className='col-sm-6'>
                            <img className='img-fluid' src={Logo} alt="LogoWorkOut" />
                        </div>
                    </div>
                </div>

            </div>

            {/* Footer */}
            <Footer className="test" />

        </>
    );
}

export default Home;
